mapper.select:
SELECT c.CHECK_CD, c.EQM_CD, c.CHCK_FG, c.CHCK_DT, c.JDGMNT, c.DISPO_CTNT, c.CHCK_PSCH, c.EQM_NM, e.chck_perd+c.chck_dt next_chck_dt FROM eqm_check c join eqm e ON c.eqm_nm = e.eqm_nm
AND c.chck_dt BETWEEN #{startDt} AND #{endDt}
AND c.eqm_nm LIKE '%'||#{eqmNm}||'%'

mapper.delete:
BEGIN
	UPDATE eqm
	SET use_yn = 'Y'
	WHERE eqm_cd = (select eqm_cd from eqm_opr where nopr_cd = #{noprCd});

	DELETE FROM eqm_opr
	WHERE nopr_cd = #{noprCd};
END;
