mapper.delete:
		BEGIN
			DELETE FROM edcts_ist
			WHERE	edcts_ist_no = #{edctsIstNo};

			UPDATE indica
			SET now_st = '생산완료'
			WHERE indica_cd IN (
  					SELECT i.indica_cd
  					FROM prcs_prog p 
  					JOIN indica i ON p.indica_cd = i.indica_cd
  					WHERE p.prcs_status = '포장검사완료'
  					AND	  i.indica_cd = #{indicaCd}
							);
		END;
